mapper.update:
update album_stat
set stat_num = stat_num + #{count}
where album_id = #{albumId}
  and stat_type = #{statType}

mapper.select:
select info.albumId, info.albumTitle, info.coverUrl, info.includeTrackCount, info.status, info.createTime, MAX(IF(info.statType = '0401', info.statNum, 0)) as playStatNum, MAX(IF(info.statType = '0402', info.statNum, 0)) as subscribeStatNum, MAX(IF(info.statType = '0403', info.statNum, 0)) as buyStatNum, MAX(IF(info.statType = '0404', info.statNum, 0)) as commentStatNum from ( select album.id as albumId, album.album_title as albumTitle, album.cover_url as coverUrl, album.include_track_count as includeTrackCount, album.status, album.create_time as createTime, stat.stat_type as statType, stat.stat_num as statNum from album_info album left join album_stat stat on stat.album_id = album.id
and album.user_id = #{vo.userId}
and album.album_title like CONCAT('%',#{vo.albumTitle},'%')
and album.status = #{vo.status}
and album.is_deleted = 0
) info group by info.albumId
